const ParseArgs = require('../basic/ParseArgs')
const { a, link } = require('sapsar/base')
const ActiveHead = require('../active/ActiveHead')

function Link(...args){
    const { content, props } = ParseArgs(args)

    if (!props.href) {
        throw new Error("Using sapsar/touch/Link: href is required.")
    }

    return a(
        props,
        content,
        ActiveHead(
            link({
                rel: "prefetch",
                href: props.href,
                as: "document",
            })
        )
    )
}

module.exports = Link