function useUpdate(res, content, selector){ 

    const scriptContent = `
    <script>
        // sapsar stream

        try{"loading"!=document.readyState?document.querySelector(\`${selector}\`).innerHTML=\`${content}\`:document.addEventListener("DOMContentLoaded",document.querySelector(\`${selector}\`).innerHTML=\`${content}\`)}catch(e){}
    </script>
    `

    res.write(scriptContent)
}

module.exports = useUpdate;
