const fs=require("fs"),path=require("path");function ScanDirectory(directoryPath){let filenames=[];function scanDirRecursive(dir,baseDir){fs.readdirSync(dir).forEach((file=>{const filePath=path.join(dir,file),relativePath=path.relative(baseDir,filePath).replace(/\\/g,"/"),stat=fs.statSync(filePath);stat.isFile()?filenames.push(relativePath):stat.isDirectory()&&scanDirRecursive(filePath,baseDir)}))}return scanDirRecursive(directoryPath,directoryPath),filenames}module.exports=ScanDirectory;